
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//For loop
		/*for(int i=100; i>=0;i=i-5)
			System.out.print(i);*/
		
		//while
		
		int i=100;
		while(i>=0){
			System.out.println(i);
			i=i-5;
		}
		//do while
		int j=100;
		do{
			System.out.println(j);
			j=j-5;
		}while(j>=0);
	}

}
