
public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i =0;i<11;i++){
		
		switch(i){		
		case 1:System.out.println("In Case 1");
			   break;
		case 2:System.out.println("In Case 2");
			break;
		case 3: System.out.println("In Case 3");
			break;
		case 8:System.out.println("In Case 8");
			break;
		case 10:System.out.println("In Case 10");
			break;
		default:
			System.out.println("No Case Defined");
		}
		}
		
	}

}
