public class SwtichCase {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Swtich case
		String[] str={"Shahriar","Jagjit","Sandeep","Tripti","Eldho","Neetika","Karam","Poonam","RandomName"};
		for(int i=0;i<str.length;i++){
			switch(str[i]){
			case "Shahriar":
					System.out.println("My name is:"+str[i]);
					break;
			case "Jagjit":
				System.out.println("My name is:"+str[i]);
				    break;
			case "Sandeep":
				System.out.println("My name is:"+str[i]);
				    break;
			case "Tripti":
				System.out.println("My name is:"+str[i]);
				    break;
			case "Eldho":
				System.out.println("My name is:"+str[i]);
				    break;
			case "Neetika":
				System.out.println("My name is:"+str[i]);
				break;
			case "Karam":
				System.out.println("My name is:"+str[i]);
				break;
			case "Poonam":
				System.out.println("My name is:"+str[i]);
				break;
			default:
				System.out.println("My name is:"+str[i]);
					
			}
		}

	}

}
